mapper.insert:
INSERT INTO record (
	r_date, 
	content, 
	user_id
)
VALUES (
	#{rDate},
	#{content},
	#{userId}
)

INSERT INTO post (record_id, r_date, content, user_id)
SELECT record_id, r_date, content, user_id
FROM record
WHERE record_id = #{recordId}
  AND user_id = #{userId}
  AND DATE(r_date) = CURDATE()

mapper.select:
SELECT record_id, r_date, content, user_id
FROM record
WHERE record_id = #{recordId}

SELECT record_id, r_date, content, user_id
FROM record
WHERE record_id = #{recordId} AND user_id = #{userId}

mapper.delete:
DELETE FROM record
WHERE user_id = #{userId}
  AND DATE(r_date) = CURDATE()
